create table plu
(
  plu_key                   bigint unsigned     not null
                            auto_increment
                            primary key,
  id                        bigint unsigned     not null,
  base_plu_key              bigint unsigned,
  link_plu_key              bigint unsigned,
  plu_types                 smallint unsigned,
  maker_key                 bigint unsigned,
  short_description         varchar(48),
  long_description          varchar(50),
  commercial_description    varchar(50),
  image_file                varchar(255),
  tax_type_key              mediumint unsigned,
  cost_decimals             smallint unsigned,
  unit_key                  smallint unsigned,  -- reserved: 1: UN,  2: KG
  price_decimals            smallint unsigned,
  adder                     bigint unsigned,
  department_key            smallint unsigned,
  suggested_prompt          varchar(80),
  data_entry_key            mediumint unsigned,
  tare_key                  mediumint unsigned,
  last_change_time          datetime,
  clerk_percent             decimal(4,2),
  conecto_flag_00           bool,             -- bit 0
  has_deposit               bool,             -- get deposit link
  has_plu_link              bool,             -- get associated PLU link
  price_required            bool,             -- enter price manually
  quantity_disallowed       bool,             -- quantity entry inhibited
  quantity_required         bool,             -- quantity entry required
  decimal_disallowed        bool,             -- disallow decimal
  decimal_required          bool,             -- require decimal
  id_required               bool,             -- require ID
  repeat_disallowed         bool,             -- disallow repeat
  scale                     bool,             -- scale PLU            bit 10
  tracking_total            bool,             -- tracking total
  deposit                   bool,             -- deposit PLU
  non_merchandise           bool,             -- non merchandise item
  return_disallowed         bool,             -- disallow return
  refund_disallowed         bool,             -- disallow refund
  markdown_disallowed       bool,             -- disallow markdown
  rebate                    bool,             -- rebate on this item
  not_for_sale              bool,             -- not for sale
  negative                  bool,             -- price is negative
  clerk_required            bool,             -- require clerk number     bit 20
  kit                       tinyint unsigned, -- sell items in list
  conecto_flag_22           bool,             -- enviar para balança
  conecto_flag_23           bool,             -- plu is delivery
  conecto_flag_24           tinyint unsigned, -- require autihorizer
  qty_from_amount           tinyint(3) unsigned, -- calc qty from ext price
  conecto_flag_26           bool,             --
  conecto_flag_27           tinyint(3) unsigned, -- complement
  conecto_flag_28           bool,             -- plu base
  conecto_flag_29           bool,             --
  conecto_flag_30           bool,             --
  conecto_flag_31           bool,             --
  valid_days                smallint unsigned,
  fiscal_class              varchar(12),
  pis_cofins                tinyint unsigned,
  quantity                  decimal(9,3),
  quantity_min              decimal(9,3),
  quantity_max              decimal(9,3),
  class                     varchar(25),
  merchandise_origin        smallint unsigned,
  merchandise_group         bigint unsigned,
  package                   char(6),
  wholesale_quantity        decimal(9,3),
  conecto_flag_32           tinyint(3) unsigned, -- entrega
  conecto_flag_33           tinyint(3) unsigned, -- é receita
  conecto_flag_34           tinyint(3) unsigned, -- é insumo
  conecto_flag_35           tinyint(3) unsigned,
  conecto_flag_36           tinyint(3) unsigned,
  conecto_flag_37           tinyint(3) unsigned,
  conecto_flag_38           tinyint(3) unsigned,
  conecto_flag_39           tinyint(3) unsigned,
  conecto_flag_40           tinyint(3) unsigned,
  message_subtotal          varchar(255),
  standard_unit_key         smallint unsigned,
  quantity_standard         decimal(9,3),
  gross_weight              decimal(7,3),
  net_weight                decimal(7,3),
  total_tax                 decimal(7,3),
  sefaz_id                  char(10),
  self_checkout_weight      decimal(7,3),
  anp_code                  char(10),
  pis_tax_type_key          mediumint unsigned,
  cofins_tax_type_key       mediumint unsigned,
  total_tax1                decimal(7,3),
  total_tax2                decimal(7,3),
  cest_code                 varchar(12),
  unique index index_plu_id (id),
  index index_plu_change (last_change_time),
  index index_plu_base (base_plu_key),
  index index_plu_completion (conecto_flag_27)
);

